package com;

public class Swapping {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		 int a=10,b=20,c;
		 // before swapping
		 
		 c=a;
		 a=b;
		 b=c;
		 
		 // after swapping
		 
		 System.out.println("after swapping: a " +  a  + " b " + b);
	}

}
