package com;

public class Typeconversion {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=20;
		long l;
		
		l=i;
		System.out.println("implicit value is.." + l);
		
		short x;
		x=(short)i;
		System.out.println("explicit value is.."+ x);
		
		
		
		
	}


}
